import React, { useState } from "react";
import { Stage, Layer, Line, Text, Group } from "react-konva";

export default function LayoutCanvas({
  layout,
  onUpdateRoom,
  selectedRoom,
  onSelectRoom,
}) {
  const width = 600, height = 450;

  // Local state for hover (must be at the top, before any return)
  const [hoveredIdx, setHoveredIdx] = useState(null);

  // Filter out rooms with less than 3 points (invalid polygons)
  const validRooms = layout.filter(room => room.points.length >= 3);

  // Compute scaling and centering
  const allPoints = validRooms.flatMap(room => room.points);
  if (allPoints.length === 0) {
    return (
      <div className="text-gray-400 text-lg">
        No valid rooms to display.
      </div>
    );
  }
  const xs = allPoints.map(p => p[0]);
  const ys = allPoints.map(p => p[1]);
  const minX = Math.min(...xs), maxX = Math.max(...xs);
  const minY = Math.min(...ys), maxY = Math.max(...ys);

  // Calculate scale to fit the canvas with some margin
  const margin = 40;
  const scale = Math.min(
    (width - margin * 2) / (maxX - minX || 1),
    (height - margin * 2) / (maxY - minY || 1)
  );
  const offsetX = margin - minX * scale + (width - (maxX - minX) * scale) / 2 - margin;
  const offsetY = margin - minY * scale + (height - (maxY - minY) * scale) / 2 - margin;

  // Helper to get centroid for label placement
  const getCentroid = (points) => {
    const n = points.length;
    const sum = points.reduce(
      (acc, [x, y]) => [acc[0] + x, acc[1] + y],
      [0, 0]
    );
    return [sum[0] / n, sum[1] / n];
  };

  // Handle drag end: update room coordinates
  const handleDragEnd = (idx, e) => {
    const node = e.target;
    const dx = node.x() / scale;
    const dy = node.y() / scale;
    const newPoints = layout[idx].points.map(([x, y]) => [x + dx, y + dy]);
    node.position({ x: 0, y: 0 });
    if (onUpdateRoom) {
      onUpdateRoom(idx, { ...layout[idx], points: newPoints });
    }
  };

  return (
    <Stage width={width} height={height}>
      <Layer>
        {validRooms.map((room, idx) => {
          // Scale and center points
          const scaledPoints = room.points.flatMap(([x, y]) => [
            x * scale + offsetX,
            y * scale + offsetY,
          ]);
          // Centroid for label
          const [cx, cy] = getCentroid(
            room.points.map(([x, y]) => [
              x * scale + offsetX,
              y * scale + offsetY,
            ])
          );
          const isSelected = selectedRoom === idx;
          const isHovered = hoveredIdx === idx;
          return (
            <Group
              key={room.name + idx}
              draggable
              onDragEnd={e => handleDragEnd(idx, e)}
              onMouseEnter={() => setHoveredIdx(idx)}
              onMouseLeave={() => setHoveredIdx(null)}
              onClick={() => onSelectRoom(idx)}
              onTap={() => onSelectRoom(idx)}
            >
              <Line
                points={scaledPoints}
                closed
                fill="#e0e7ff"
                stroke={
                  isSelected ? "#f59e42" : isHovered ? "#2563eb" : "#1e293b"
                }
                strokeWidth={isSelected ? 4 : isHovered ? 3 : 2}
                shadowBlur={isSelected ? 8 : 0}
              />
              <Text
                text={room.name}
                x={cx - room.name.length * 4}
                y={cy - 8}
                fontSize={16}
                fill="#1e293b"
                fontStyle="bold"
              />
            </Group>
          );
        })}
      </Layer>
    </Stage>
  );
}
