// src/utils/layoutHelpers.js

// Snap a point to the nearest grid
export function snapPointToGrid(val, gridSize) {
  return Math.round(val / gridSize) * gridSize;
}

// Get a color based on room type/name
export function getRoomColor(room) {
  const type = (room.type || room.name || "").toLowerCase();
  if (type.includes("bedroom")) return "#fef9c3";
  if (type.includes("living")) return "#bae6fd";
  if (type.includes("kitchen")) return "#bbf7d0";
  if (type.includes("bathroom")) return "#fbcfe8";
  if (type.includes("corridor")) return "#ddd6fe";
  return "#e0e7ff";
}
