// src/RoomDetails.jsx
import React, { useState, useEffect } from "react";

const ROOM_TYPES = [
  "bedroom",
  "living_room",
  "kitchen",
  "bathroom",
  "corridor",
  "custom",
];

export default function RoomDetails({ room, onUpdate }) {
  const [localRoom, setLocalRoom] = useState(room);

  useEffect(() => {
    setLocalRoom(room);
  }, [room]);

  if (!room) {
    return (
      <div className="w-64 p-4 border-l border-gray-200 bg-white">
        <div className="text-gray-400">Select a room to see details.</div>
      </div>
    );
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalRoom({ ...localRoom, [name]: value });
  };

  const handleTypeChange = (e) => {
    setLocalRoom({ ...localRoom, type: e.target.value });
  };

  const handleSave = () => {
    onUpdate(localRoom);
  };

  return (
    <div className="w-64 p-4 border-l border-gray-200 bg-white">
      <h3 className="font-bold text-lg mb-2">Room Details</h3>
      <div className="mb-2">
        <label className="block text-sm font-medium">Name</label>
        <input
          className="border rounded px-2 py-1 w-full"
          name="name"
          value={localRoom.name}
          onChange={handleChange}
        />
      </div>
      <div className="mb-2">
        <label className="block text-sm font-medium">Type</label>
        <select
          className="border rounded px-2 py-1 w-full"
          value={localRoom.type || "custom"}
          onChange={handleTypeChange}
        >
          {ROOM_TYPES.map((type) => (
            <option key={type}>{type}</option>
          ))}
        </select>
      </div>
      <button
        className="bg-blue-600 text-white px-3 py-1 rounded mt-2"
        onClick={handleSave}
      >
        Save
      </button>
    </div>
  );
}
