import React, { useRef, useState } from "react";
import { Stage, Layer, Line, Text, Group, Rect } from "react-konva";
import { getRoomColor, snapPointToGrid } from "./utils/layoutHelpers";

const CANVAS_WIDTH = 600;
const CANVAS_HEIGHT = 450;
const GRID_SIZE = 25;

export default function LayoutCanvas({
  layout,
  onSelectRoom,
  selectedRoom,
  onDeleteRoom,
  onUpdateRoom,
}) {
  // Zoom and pan
  const stageRef = useRef();
  const [scale, setScale] = useState(1);
  const [stagePos, setStagePos] = useState({ x: 0, y: 0 });
  const [hoveredRoom, setHoveredRoom] = useState(null);

  // Filter out rooms with less than 3 points
  const validRooms = layout.filter((room) => room.points.length >= 3);

  // Scaling and centering logic
  const allPoints = validRooms.flatMap((room) => room.points);
  if (allPoints.length === 0) {
    return (
      <div className="text-gray-400 text-lg">
        No valid rooms to display.
      </div>
    );
  }
  const xs = allPoints.map((p) => p[0]);
  const ys = allPoints.map((p) => p[1]);
  const minX = Math.min(...xs),
    maxX = Math.max(...xs);
  const minY = Math.min(...ys),
    maxY = Math.max(...ys);

  const margin = 40;
  const fitScale = Math.min(
    (CANVAS_WIDTH - margin * 2) / (maxX - minX || 1),
    (CANVAS_HEIGHT - margin * 2) / (maxY - minY || 1)
  );
  // Use fitScale for initial fit, then user can zoom
  const displayScale = scale * fitScale;
  const offsetX =
    margin - minX * displayScale +
    (CANVAS_WIDTH - (maxX - minX) * displayScale) / 2 -
    margin +
    stagePos.x;
  const offsetY =
    margin - minY * displayScale +
    (CANVAS_HEIGHT - (maxY - minY) * displayScale) / 2 -
    margin +
    stagePos.y;

  // Grid lines
  const gridLines = [];
  for (let i = 0; i < CANVAS_WIDTH / (GRID_SIZE * displayScale); i++) {
    gridLines.push(
      <Line
        key={"v" + i}
        points={[
          i * GRID_SIZE * displayScale,
          0,
          i * GRID_SIZE * displayScale,
          CANVAS_HEIGHT,
        ]}
        stroke="#e5e7eb"
        strokeWidth={1}
      />
    );
  }
  for (let i = 0; i < CANVAS_HEIGHT / (GRID_SIZE * displayScale); i++) {
    gridLines.push(
      <Line
        key={"h" + i}
        points={[
          0,
          i * GRID_SIZE * displayScale,
          CANVAS_WIDTH,
          i * GRID_SIZE * displayScale,
        ]}
        stroke="#e5e7eb"
        strokeWidth={1}
      />
    );
  }

  // Helper: centroid for label
  const getCentroid = (points) => {
    const n = points.length;
    const sum = points.reduce(
      (acc, [x, y]) => [acc[0] + x, acc[1] + y],
      [0, 0]
    );
    return [sum[0] / n, sum[1] / n];
  };

  // Drag/resize logic
  const handleDragMove = (idx, e) => {
    const node = e.target;
    const dx = node.x() - node._lastPos.x;
    const dy = node.y() - node._lastPos.y;
    node._lastPos = { x: node.x(), y: node.y() };
    const newPoints = layout[idx].points.map(([x, y]) => [
      snapPointToGrid(x + dx / displayScale, GRID_SIZE),
      snapPointToGrid(y + dy / displayScale, GRID_SIZE),
    ]);
    onUpdateRoom(idx, { ...layout[idx], points: newPoints });
  };

  const handleDragStart = (idx, e) => {
    e.target._lastPos = { x: e.target.x(), y: e.target.y() };
  };

  // Zoom and pan handlers
  const handleWheel = (e) => {
    e.evt.preventDefault();
    const scaleBy = 1.05;
    const oldScale = scale;
    const mousePointTo = {
      x: (e.evt.x - stageRef.current.x()) / oldScale,
      y: (e.evt.y - stageRef.current.y()) / oldScale,
    };
    const newScale =
      e.evt.deltaY > 0 ? oldScale / scaleBy : oldScale * scaleBy;
    setScale(Math.max(0.5, Math.min(3, newScale)));
    setStagePos({
      x: e.evt.x - mousePointTo.x * newScale,
      y: e.evt.y - mousePointTo.y * newScale,
    });
  };

  const handleStageDragEnd = (e) => {
    setStagePos({ x: e.target.x(), y: e.target.y() });
  };

  // Tooltip
  const [tooltip, setTooltip] = useState({ visible: false, x: 0, y: 0, text: "" });

  return (
    <Stage
      width={CANVAS_WIDTH}
      height={CANVAS_HEIGHT}
      ref={stageRef}
      scaleX={1}
      scaleY={1}
      draggable
      x={stagePos.x}
      y={stagePos.y}
      onWheel={handleWheel}
      onDragEnd={handleStageDragEnd}
      style={{ background: "#f9fafb", borderRadius: 8 }}
    >
      <Layer>
        {/* Grid */}
        {gridLines}
      </Layer>
      <Layer>
        {validRooms.map((room, idx) => {
          const scaledPoints = room.points.flatMap(([x, y]) => [
            x * displayScale + offsetX,
            y * displayScale + offsetY,
          ]);
          const [cx, cy] = getCentroid(
            room.points.map(([x, y]) => [
              x * displayScale + offsetX,
              y * displayScale + offsetY,
            ])
          );
          const isSelected = selectedRoom === idx;
          const isHovered = hoveredRoom === idx;
          return (
            <Group
              key={room.name + idx}
              draggable
              onDragStart={(e) => handleDragStart(idx, e)}
              onDragMove={(e) => handleDragMove(idx, e)}
              onClick={() => onSelectRoom(idx)}
              onTap={() => onSelectRoom(idx)}
              onMouseEnter={() => {
                setHoveredRoom(idx);
                setTooltip({
                  visible: true,
                  x: cx,
                  y: cy - 30,
                  text: `Room: ${room.name}`,
                });
              }}
              onMouseLeave={() => {
                setHoveredRoom(null);
                setTooltip({ visible: false, x: 0, y: 0, text: "" });
              }}
            >
              <Line
                points={scaledPoints}
                closed
                fill={getRoomColor(room)}
                stroke={isSelected ? "#f59e42" : isHovered ? "#2563eb" : "#1e293b"}
                strokeWidth={isSelected ? 4 : 2}
                shadowBlur={isSelected ? 10 : 0}
              />
              <Text
                text={room.name}
                x={cx - room.name.length * 4}
                y={cy - 8}
                fontSize={16}
                fill="#1e293b"
                fontStyle="bold"
              />
              {/* Delete Button (top-right of polygon) */}
              {isSelected && (
                <Rect
                  x={Math.max(...scaledPoints.filter((_, i) => i % 2 === 0)) - 10}
                  y={Math.min(...scaledPoints.filter((_, i) => i % 2 === 1)) - 10}
                  width={20}
                  height={20}
                  fill="#f87171"
                  cornerRadius={4}
                  onClick={() => onDeleteRoom(idx)}
                  onTap={() => onDeleteRoom(idx)}
                />
              )}
              {isSelected && (
                <Text
                  text="ðŸ—‘"
                  x={Math.max(...scaledPoints.filter((_, i) => i % 2 === 0)) - 6}
                  y={Math.min(...scaledPoints.filter((_, i) => i % 2 === 1)) - 10}
                  fontSize={16}
                  onClick={() => onDeleteRoom(idx)}
                  onTap={() => onDeleteRoom(idx)}
                  style={{ cursor: "pointer" }}
                />
              )}
            </Group>
          );
        })}
        {/* Tooltip */}
        {tooltip.visible && (
          <Group>
            <Rect
              x={tooltip.x - 40}
              y={tooltip.y - 24}
              width={80}
              height={22}
              fill="#fff"
              stroke="#888"
              cornerRadius={4}
              shadowBlur={2}
            />
            <Text
              text={tooltip.text}
              x={tooltip.x - 36}
              y={tooltip.y - 22}
              fontSize={14}
              fill="#1e293b"
            />
          </Group>
        )}
      </Layer>
    </Stage>
  );
}
