import React from "react";
import { Stage, Layer, Line, Text } from "react-konva";

export default function LayoutCanvas({ layout }) {
  // Optionally, auto-size the canvas based on content
  const width = 600, height = 450;

  return (
    <Stage width={width} height={height}>
      <Layer>
        {layout.map((room, idx) => (
          <React.Fragment key={room.name + idx}>
            <Line
              points={room.points.flat()}
              closed
              fill="#e0e7ff"
              stroke="#1e293b"
              strokeWidth={2}
            />
            <Text
              text={room.name}
              x={room.points[0][0] + 5}
              y={room.points[0][1] + 5}
              fontSize={16}
              fill="#1e293b"
              fontStyle="bold"
            />
          </React.Fragment>
        ))}
      </Layer>
    </Stage>
  );
}
