// src/App.jsx
import React, { useState } from "react";
import axios from "axios";
import LayoutCanvas from "./LayoutCanvas";
import RoomDetails from "./RoomDetails";
import "./App.css";

function App() {
  // Chat state
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [layout, setLayout] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState(null);

  // Undo/redo stacks
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);

  // Handle sending a message
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMsg = { sender: "user", text: input };
    setMessages((msgs) => [...msgs, userMsg]);
    setLoading(true);

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", { prompt: input });
      const botMsg = { sender: "bot", text: res.data.response };
      setMessages((msgs) => [...msgs, botMsg]);
      setUndoStack((stack) => [...stack, layout]);
      setLayout(res.data.layout || []);
      setRedoStack([]); // Clear redo on new action
      setSelectedRoom(null);
    } catch (err) {
      setMessages((msgs) => [
        ...msgs,
        { sender: "bot", text: "Error: Could not reach backend." },
      ]);
      setLayout([]);
    }
    setInput("");
    setLoading(false);
  };

  // Undo/redo handlers
  const handleUndo = () => {
    if (undoStack.length === 0) return;
    setRedoStack((stack) => [layout, ...stack]);
    setLayout(undoStack[undoStack.length - 1]);
    setUndoStack((stack) => stack.slice(0, -1));
    setSelectedRoom(null);
  };
  const handleRedo = () => {
    if (redoStack.length === 0) return;
    setUndoStack((stack) => [...stack, layout]);
    setLayout(redoStack[0]);
    setRedoStack((stack) => stack.slice(1));
    setSelectedRoom(null);
  };

  // Add/delete room handlers
  const handleAddRoom = () => {
    const newRoom = {
      name: `room${layout.length + 1}`,
      points: [
        [100, 100],
        [200, 100],
        [200, 200],
        [100, 200],
      ],
      type: "custom",
    };
    setUndoStack((stack) => [...stack, layout]);
    setLayout([...layout, newRoom]);
    setRedoStack([]);
    setSelectedRoom(layout.length);
  };

  const handleDeleteRoom = (idx) => {
    setUndoStack((stack) => [...stack, layout]);
    setLayout(layout.filter((_, i) => i !== idx));
    setRedoStack([]);
    setSelectedRoom(null);
  };

  // Update room (drag/resize/edit)
  const handleUpdateRoom = (idx, updatedRoom) => {
    setUndoStack((stack) => [...stack, layout]);
    setLayout(layout.map((room, i) => (i === idx ? updatedRoom : room)));
    setRedoStack([]);
  };

  return (
    <div className="flex h-screen">
      {/* Left: Chat Window */}
      <div className="w-1/3 border-r border-gray-200 flex flex-col bg-white">
        <h2 className="text-2xl font-bold m-4">Infraabot Chat</h2>
        <div className="flex-1 overflow-y-auto p-4 space-y-2">
          {messages.map((msg, idx) => (
            <div
              key={idx}
              className={`p-2 rounded ${
                msg.sender === "user"
                  ? "bg-blue-100 text-right ml-8"
                  : "bg-gray-100 text-left mr-8"
              }`}
            >
              {msg.text}
            </div>
          ))}
          {loading && <div className="text-gray-400">Infraabot is typing...</div>}
        </div>
        <form onSubmit={sendMessage} className="p-4 flex gap-2 border-t">
          <input
            className="flex-1 border rounded px-2 py-1"
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your message..."
            disabled={loading}
          />
          <button
            className="bg-blue-600 text-white px-4 py-1 rounded disabled:opacity-50"
            type="submit"
            disabled={loading || !input.trim()}
          >
            Send
          </button>
        </form>
        <div className="flex gap-2 p-4 border-t">
          <button
            className="bg-gray-200 px-2 py-1 rounded"
            onClick={handleUndo}
            disabled={undoStack.length === 0}
          >
            Undo
          </button>
          <button
            className="bg-gray-200 px-2 py-1 rounded"
            onClick={handleRedo}
            disabled={redoStack.length === 0}
          >
            Redo
          </button>
          <button
            className="bg-green-500 text-white px-2 py-1 rounded"
            onClick={handleAddRoom}
          >
            Add Room
          </button>
        </div>
      </div>

      {/* Right: Layout Canvas and Room Details */}
      <div className="w-2/3 flex flex-row bg-gray-50">
        <div className="flex-1 flex flex-col">
          <h2 className="text-2xl font-bold m-4">Layout Preview</h2>
          <div className="flex-1 flex items-center justify-center">
            {layout.length > 0 ? (
              <LayoutCanvas
                layout={layout}
                onSelectRoom={setSelectedRoom}
                selectedRoom={selectedRoom}
                onDeleteRoom={handleDeleteRoom}
                onUpdateRoom={handleUpdateRoom}
              />
            ) : (
              <div className="text-gray-400 text-lg">
                Send a prompt to see your layout!
              </div>
            )}
          </div>
        </div>
        <RoomDetails
          room={layout[selectedRoom]}
          onUpdate={(updatedRoom) =>
            handleUpdateRoom(selectedRoom, updatedRoom)
          }
        />
      </div>
    </div>
  );
}

export default App;
